import { file } from "@babel/types";
import React, { useEffect, useState } from "react";
import ImageSelector from "./ImageSelector";
import { Transition } from "semantic-ui-react";

const Place_Definer = ({
  Card_State,
  visible,
  OnClosed,
  OnSumbit,
  OnShopNameChange,
  OnShopTypeChange
}) => {
  const [id, setid] = useState();
  const [ImageSrc, setImageSrc] = useState([]);
  const [ShopName, setShopName] = useState();
  const [ShopType, setShopType] = useState();

  useEffect(() => {
    setid(Card_State.id);
    setImageSrc(Card_State.ImageSrc);
    setShopName(Card_State.ShopName);
    setShopType(Card_State.ShopType);
    console.log(">>>>>>>ImageSrc Change from Definer<<<<<<<<");
    console.log(Card_State.ImageSrc);
    console.log(ImageSrc);
    console.log("------ImageSrc Change------");
  }, [ImageSrc]);

  const OnImageChange = () => {
    const fileSelector = document.getElementById("fileSelector");
    const Images = fileSelector.files;
    let ImageBaseResult = [];
    ImageBaseResult = ImageBaseResult.concat(ImageSrc);
    for (let i = 0; i < Images.length; i++) {
      const reader = new FileReader();
      reader.readAsDataURL(Images[i]);
      reader.onload = function() {
        ImageBaseResult.push(reader.result);
      };
    }
    console.log(">>>>>>>>>>>Image input Change<<<<<<<<<<<<");
    console.log(ImageBaseResult);
    console.log("-----------Image input Change-----------");
    onChange({
      id: "1",
      Lat: "2",
      Lng: "",
      Address: "",
      ShopName: Card_State.ShopName,
      ShopType: Card_State.ShopType,
      ImageSrc: { ImageBaseResult }
    });
  };

  // const OnImageDelete = index => {
  //   let _ImageSrcs = ImageSrc;
  //   _ImageSrcs.splice(index, 1);
  //   onChange({
  //     id: "",
  //     Lat: "",
  //     Lng: "",
  //     Address: "",
  //     ShopName: Card_State.ShopName,
  //     ShopType: Card_State.ShopType,
  //     ImageSrc: _ImageSrcs
  //   });
  // };

  // const OnShopNameChange = E => {
  //   onChange({
  //     id: "",
  //     Lat: "",
  //     Lng: "",
  //     Address: "",
  //     ShopName: E.target.value,
  //     ShopType: Card_State.ShopType,
  //     ImageSrc: Card_State.ImageSrc
  //   });
  // };
  // const OnShopTypeChange = E => {
  //   onChange({
  //     id: "",
  //     Lat: "",
  //     Lng: "",
  //     Address: "",
  //     ShopName: Card_State.ShopName,
  //     ShopType: E.target.value,
  //     ImageSrc: Card_State.ImageSrc
  //   });
  // };

  const ShopTypeOptions = [
    {
      value: "BAR",
      name: "Bar"
    },
    {
      value: "RESTR",
      name: "Restaurant"
    },
    {
      value: "SMKT",
      name: "Supermarket"
    },
    {
      value: "BTQ",
      name: "Boutique"
    }
  ];

  const ShopTypes = ShopTypeOptions.map(Element => {
    return <option value={Element.value}>{Element.name}</option>;
  });

  return (
    <Transition visible={visible} animation="fade up" duration={500}>
      <div
        style={{
          position: "absolute",
          bottom: "0%",
          width: "100%"
        }}
      >
        <div className="ui segment">
          <i
            onClick={OnClosed}
            style={{
              position: "absolute",
              right: "50%",
              transform: "translate(+50%, -50%) scale(1.3)"
            }}
            className="caret down icon"
          ></i>
          <br />
          <div className="ui form">
            <div className="ui grid">
              <div className="two column row">
                <div className="column">
                  <div className="field">
                    <label>Shop name</label>
                    <input
                      type="text"
                      name="SHOP-NAME"
                      placeholder="e.g., Ray's Bar"
                      value={ShopName}
                      onChange={OnShopNameChange}
                    />
                  </div>
                </div>
                <div className="column">
                  <div className="field">
                    <label>Shop Type</label>
                    <select
                      class="ui fluid dropdown"
                      onChange={OnShopTypeChange}
                    >
                      {ShopTypes}
                    </select>
                  </div>
                </div>
              </div>
              <ImageSelector
                ImageSrc={ImageSrc}
                onChange={OnImageChange}
                OnImageDelete={OnImageDelete}
              />
            </div>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Place_Definer;
