const Images_preview = ({ Images, OnClick }) => {
  const [Left, setLeft] = useState(0);
  let _Left = 0;
  const Images_Show = Array.from(Images).map((Element, index) => {
    if (index <= 5) {
      return <img className="ui small image" key={index} src={Element} />;
    } else {
      _Left = Math.ceil(_Left + 1);
    }
  });
  if (Left === 0) {
    return (
      <div
        style={{ cursor: "pointer" }}
        onClick={OnClick}
        className="ui mini images"
      >
        {Images_Show}
      </div>
    );
  } else {
    return (
      <div
        style={{ cursor: "pointer" }}
        onClick={OnClick}
        className="ui mini images"
      >
        {Images_Show}
        {`+${Left}`}
      </div>
    );
  }
};

