import { file } from "@babel/types";
import React, { useEffect, useState } from "react";
import ImageSelector from "./ImageSelector";
import { Transition } from "semantic-ui-react";

const Place_Definer = ({
  _ShopName,
  _ShopType,
  _ImageSrc,
  visible,
  OnClosed,
  OnSumbit,
  OnShopNameChange,
  OnShopTypeChange,
  OnImageSrcChange
}) => {
  // const [id, setid] = useState();
  const [ImageSrc, setImageSrc] = useState(_ImageSrc);
  const [ShopName, setShopName] = useState(_ShopName);
  const [ShopType, setShopType] = useState(_ShopType);

  // useEffect(() => {
  //   setImageSrc(_ImageSrc);
  //   setShopName(_ShopName);
  //   setShopType(_ShopType);
  //   console.log(">>>>>>>ImageSrc Change from Definer<<<<<<<<");
  //   console.log(ImageSrc);
  //   console.log("------ImageSrc Change------");
  // });

  const OnImageDelete = index => {
    let ImageSrcs = ImageSrc;
    ImageSrcs.splice(index, 1);
    setImageSrc(ImageSrcs);
    OnImageSrcChange(ImageSrcs);
  };

  const ShopTypeOptions = [
    {
      value: "BAR",
      name: "Bar"
    },
    {
      value: "RESTR",
      name: "Restaurant"
    },
    {
      value: "SMKT",
      name: "Supermarket"
    },
    {
      value: "BTQ",
      name: "Boutique"
    }
  ];

  const ShopTypes = ShopTypeOptions.map(Element => {
    return <option value={Element.value}>{Element.name}</option>;
  });

  return (
    <Transition visible={visible} animation="fade up" duration={500}>
      <div
        style={{
          position: "absolute",
          bottom: "0%",
          width: "100%"
        }}
      >
        <div className="ui segment">
          <i
            onClick={OnClosed}
            style={{
              position: "absolute",
              right: "50%",
              transform: "translate(+50%, -50%) scale(1.3)"
            }}
            className="caret down icon"
          ></i>
          <br />
          <div className="ui form">
            <div className="ui grid">
              <div className="two column row">
                <div className="column">
                  <div className="field">
                    <label>Shop name</label>
                    <input
                      type="text"
                      name="SHOP-NAME"
                      placeholder="e.g., Ray's Bar"
                      value={ShopName}
                      onChange={E => {
                        setShopName(E.target.value);
                        OnShopNameChange(E);
                      }}
                    />
                  </div>
                </div>
                <div className="column">
                  <div className="field">
                    <label>Shop Type</label>
                    <select
                      className="ui fluid dropdown"
                      onChange={E => {
                        setShopType(E.target.value);
                        OnShopTypeChange(E);
                      }}
                      value={ShopType}
                    >
                      {ShopTypes}
                    </select>
                  </div>
                </div>
              </div>
              <ImageSelector
                Value={ImageSrc}
                onChange={E => {
                  setImageSrc(E.target.value);
                  OnImageSrcChange(E);
                }}
                OnImageDelete={OnImageDelete}
              />
            </div>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Place_Definer;
