import React, { useRef, useEffect, useState } from "react";
// import mapboxgl from "mapbox-gl";
import ReactMapboxGl, { Layer, Feature } from 'react-mapbox-gl';
import 'mapbox-gl/dist/mapbox-gl.css';


const Map = ({ OnCenterChange, Lat, Lng }) => {
  const [Width, setWidth] = useState(document.documentElement.clientWidth);
  const [Height, setHeight] = useState(document.documentElement.clientHeight);
  const map = useRef();

  useEffect(() => {
    const setWindowSize = () => {
      setWidth(window.outerWidth);
      setHeight(window.outerHeight);
    };
    window.addEventListener("resize", setWindowSize);
    return () => {
      window.removeEventListener("resize", setWindowSize);
    };
  }, [Width, Height]);

  useEffect(() => {
    mapboxgl.accessToken =
      "pk.eyJ1IjoiZGVubmlzY2hhbmc5MjgiLCJhIjoiY2tzanRjanRiMXpsbDJvbnVxdTgxNHh4byJ9.AcPK9uy9CBN1DUX8g6G-_Q";

    let map = new mapboxgl.Map({
      container: "map",
      style: "mapbox://styles/mapbox/dark-v10", // style URL
      zoom: 20 // starting zoom
    }).setCenter([Lng, Lat])
      .addControl(new mapboxgl.FullscreenControl());
    // map.current.on("move", () => {
    //   OnCenterChange(map.current.getCenter().lat, map.current.getCenter().lng);
    // });
    // map.current.setStyle("mapbox://styles/mapbox/outdoors-v11");
    // map.current

    var marker = new mapboxgl.Marker();
    marker.setLngLat([113.53591839930641, 22.189516348074037]);
    marker.addTo(map);

  }, []);

  return <div id="map" style={{
    position: "fixed", top: "0", bottom: "0", width: "100%"
  }} > </div>
};

export default Map;
