import { file } from "@babel/types";
import React, { useEffect, useState } from "react";
import ImageSelector from "./ImageSelector";
import { Transition } from "semantic-ui-react";

const Place_Definer = ({ visible, OnClosed, OnSumbit, Lat, Lng }) => {
  const [ImageSrc, setImageSrc] = useState([]);
  const [ShopName, setShopName] = useState();
  const [ShopType, setShopType] = useState();
  const [ShopDescription, setShopDescription] = useState();

  const OnShopNameChange = E => {
    setShopName(E.target.value);
  };

  const OnShopTypeChange = E => {
    setShopType(E.target.value);
  };
  const OnShopDescriptionChange = E => {
    setShopDescription(E.target.value);
  };
  const OnImageSrcChange = E => {
    setImageSrc(E);
    console.log(">>>>>>>>>>>OnImageSrc Called Change<<<<<<<<<");
    console.log(E);
  };

  const OnImageDelete = index => {
    setImageSrc(ImageSrc.filter((e, i) => i != index));
  };

  const ShopTypeOptions = [
    {
      value: "BAR",
      name: "Bar"
    },
    {
      value: "RESTR",
      name: "Restaurant"
    },
    {
      value: "SMKT",
      name: "Supermarket"
    },
    {
      value: "BTQ",
      name: "Boutique"
    }
  ];

  const ShopTypes = ShopTypeOptions.map(Element => {
    return <option value={Element.value}>{Element.name}</option>;
  });

  return (
    <Transition visible={visible} animation="fade up" duration={500}>
      <div class="ui active inline loader">
        <div
          style={{
            position: "absolute",
            bottom: "0%",
            width: "100%"
          }}
        >
          <div className="ui segment">
            <i
              onClick={OnClosed}
              style={{
                position: "absolute",
                right: "50%",
                transform: "translate(+50%, -50%) scale(1.3)"
              }}
              className="caret down icon"
            ></i>
            <br />
            <div className="ui form">
              <div className="ui grid">
                <div className="two column row">
                  <div className="column">
                    <div className="field">
                      <label>Shop name</label>
                      <input
                        type="text"
                        name="SHOP-NAME"
                        placeholder="e.g., Ray's Bar"
                        value={ShopName}
                        onChange={OnShopNameChange}
                      />
                    </div>
                  </div>
                  <div className="column">
                    <div className="field">
                      <label>Shop Type</label>
                      <select
                        className="ui fluid dropdown"
                        onChange={OnShopTypeChange}
                        value={ShopType}
                      >
                        {ShopTypes}
                      </select>
                    </div>
                  </div>
                </div>
                <ImageSelector
                  Value={ImageSrc}
                  onChange={OnImageSrcChange}
                  OnImageDelete={OnImageDelete}
                />
                <div className="two column row">
                  <div className="fourteen wide column">
                    <div className="field">
                      <label>Description</label>
                      <textarea
                        rows="2"
                        value={ShopDescription}
                        onChange={OnShopDescriptionChange}
                      ></textarea>
                    </div>
                  </div>
                  <div className="two wide column">
                    <i
                      onClick={() => {
                        OnSumbit({
                          ShopName: ShopName,
                          ShopType: ShopType,
                          ImageSrc: ImageSrc,
                          ShopDescription: ShopDescription,
                          Lat: Lat,
                          Lng: Lng
                        });
                      }}
                      className="paper plane outline icon"
                      style={{
                        transform: "scale(2)",
                        position: "absolute",
                        bottom: "35%",
                        left: "25%"
                      }}
                    ></i>
                  </div>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Transition >
  );
};

export default Place_Definer;
