        if (DisplayingImage === 0 || DisplayingImage === ImageSrc.length) return;
        setDisplayingImage(DisplayingImage - 1);
    }
    const OnRigthArrowClicked = () => {
        if (DisplayingImage === 0 || DisplayingImage === ImageSrc.length) return;
        setDisplayingImage(DisplayingImage + 1);
    }
    console.log(ImageSrc);
