// import React from "react";
import { Transition } from "semantic-ui-react";
import ImageViewer from "./ImageViewer";

const CompanyCard = ({ Company, State, onClosed }) => {
    console.log(Company);
    return (
        <Transition visible={State !== 0 ? true : false} animation="scale" duration={500}>
            <div
                className="ui segment"
                style={{
                    zIndex: "2",
                    position: "fixed",

                    top: "5%",
                    bottom: "5%",

                    left: "5%",
                    right: "5%"
                }}
            >
                <button
                    className="ui grey small labeled icon button"
                    onClick={onClosed}>
                    <i className="reply icon"></i>
                    Back
                </button>
                <div class="ui big label">
                    <i class="mail icon"></i> 23
                </div>
                <h6> </h6>
                <div style={{ grid: "10px" }} className="ui grid">
                    <ImageViewer ImageSrc={Company.ImageSrc} />
                    <div className="row">
                        <div className='column'>
                            <h2>{Company.ShopName}</h2>
                            <br />
                            {Company.ShopDescription}
                        </div>
                    </div>
                </div>
            </div>
        </Transition>
    )
}

export default CompanyCard
