import useSelection from "antd/lib/table/hooks/useSelection";
import React, { useEffect, useState } from "react";
import Images_preview from "./Image_preview";
import { ReactComponent as Add_ImageIcon } from "./Add_Image.svg";
import { auto } from "async";

// import

const ImageSelector = ({ ImageSrc, onChange }) => {
  const [ImageSrcs, setImageSrcs] = useState(ImageSrc);
  const OnImageUpload = () => {
    const fileSelector = document.getElementById("fileSelector");
    const Images = Array.from(fileSelector.files);
    let ImageBaseResult = ImageSrc;
    for (let i = 0; i < Images.length; i++) {
      const reader = new FileReader();
      reader.readAsDataURL(Images[i]);
      reader.onload = function() {
        ImageBaseResult.push(reader.result);
      };
    }
    setImageSrcs(ImageBaseResult);
    onChange(ImageBaseResult);
  };

  return (
    <div className="two column row">
      <div className="eight wide column">
        <Images_preview Images={ImageSrcs} />
      </div>
      <div className="eight wide column">
        <div className="ui black vertical animated button" tabindex="1">
          <div className="hidden content">
            Upload image
            <input
              type="file"
              id="fileSelector"
              className="fileSelector"
              data-target="file-uploader"
              accept="image/*"
              multiple
              onChange={OnImageUpload}
            />
          </div>
          <div
            className="visible content"
            style={{ width: auto, innerHeight: auto }}
          >
            <i className="images outline icon"></i>
          </div>
          {/* Upload image */}
        </div>
      </div>
    </div>
  );
};
export default ImageSelector;
