import React, { useState } from 'react';

const ImageViewer = ({ ImageSrc }) => {
    const [DisplayingImageIndex, setDisplayingImageIndex] = useState(0);
    const OnLeftArrowClicked = () => {
        if (DisplayingImageIndex === 0 || DisplayingImageIndex === ImageSrc.length) return;
        setDisplayingImageIndex(DisplayingImageIndex - 1);
    }
    const OnRigthArrowClicked = () => {
        if (DisplayingImageIndex === 0 || DisplayingImageIndex === ImageSrc.length) return;
        setDisplayingImageIndex(DisplayingImageIndex + 1);
    }

    console.log(DisplayingImageIndex);
    return (
        <div className="row">
            <div onClick={OnLeftArrowClicked} className="two wide column">

                <i
                    style={{
                        position: "absolute",

                        left: '50%',
                        bottom: '50%',
                    }}
                    className="caret square left icon"></i>
            </div>
            <div className="twelve wide column">
                <img
                    style={{
                        display: 'block',
                        marginLeft: 'auto',
                        marginRight: 'auto',
                        Width: "auto",
                        height: "auto"
                    }}

                    src={ImageSrc.length !== 0 ? ImageSrc[DisplayingImageIndex] : "https://www.wddonline.com/content/uploads/2020/08/placeholder-image.png"}
                />
            </div>
            <div onClick={OnRigthArrowClicked} className="two wide column">

                <i
                    style={{
                        position: "absolute",

                        right: '50%',
                        bottom: '50%',
                    }}
                    className="caret square right icon" ></i>
            </div>
        </div >

    )
}

export default ImageViewer;
