import React, { useRef, useEffect, useState } from "react";
import mapboxgl from "mapbox-gl";
import Spinner from "./Loading_Spinner";
const Map = ({ OnCenterChange, Lat, Lng }) => {
  const mapContainer = useRef(null);

  const [Width, setWidth] = useState(window.innerWidth);
  const [Height, setHeight] = useState(window.innerHeight);
  const [map, setmap] = useState(null);
  const windowSize = useRef(null);

  const setPosition = () => {
    window.navigator.geolocation.getCurrentPosition(
      position => {
        setCurrentLng(position.coords.longitude);
        setCurrentLat(position.coords.latitude);
      },
      err => {
        setError(err.message);
      }
    );
  };

  useEffect(() => {
    setPosition();
  }, []);

  useEffect(() => {
    const setWindowSize = () => {
      setWidth(window.innerWidth);
      setHeight(window.innerHeight);
    };
    window.addEventListener("resize", setWindowSize);
    return () => {
      window.removeEventListener("resize", setWindowSize);
    };
  }, [Width, Height]);

  useEffect(() => {
    if (map === null && CurrentLng !== null && CurrentLat !== null) {
      mapboxgl.accessToken =
        "pk.eyJ1IjoiZGVubmlzY2hhbmc5MjgiLCJhIjoiY2tzanRjanRiMXpsbDJvbnVxdTgxNHh4byJ9.AcPK9uy9CBN1DUX8g6G-_Q";

      setmap(
        new mapboxgl.Map({
          container: "map",
          style: "mapbox://styles/mapbox/streets-v11", // style URL
          center: [CurrentLng, CurrentLat], // starting position [lng, lat]
          zoom: 14 // starting zoom
        })
      );
    } else if (map !== null) {
      map.on("move", () => {
        setCurrentLng(map.getCenter().lng.toFixed(4));
        setCurrentLat(map.getCenter().lat.toFixed(4));
      });
    }
  });
  if (CurrentLng !== null && CurrentLat !== null) {
    return <div id="map" style={{ width: Width, height: Height }} />;
  } else {
    return (
      <div style={{ width: Width, height: Height }}>
        <Spinner message={Error} />
      </div>
    );
  }
};

export default Map;
