import React, { useRef, useEffect, useState } from "react";
import mapboxgl from "mapbox-gl";

const Map = () => {
  const mapContainer = useRef(null);
  const [CurrentLng, setCurrentLng] = useState(null);
  const [CurrentLat, setCurrentLat] = useState(null);
  const [Error, setError] = useState(null);
  const [Width, setWidth] = useState(window.innerWidth);
  const [Height, setHeight] = useState(window.innerHeight);
  const map = useRef(null);
  const windowSize = useRef(null);

  const setPosition = () => {
    window.navigator.geolocation.getCurrentPosition(position => {
      setCurrentLng(position.coords.longitude);
      setCurrentLat(position.coords.latitude);
    });
  };

  useEffect(() => {
    setPosition();
  }, []);

  useEffect(() => {
    const setWindowSize = () => {
      setWidth(window.innerWidth);
      setHeight(window.innerHeight);
    };
    console.log(window.innerWidth);
    window.addEventListener("resize", setWindowSize);
    return () => {
      window.removeEventListener("resize", setWindowSize);
    };
  }, [Width, Height]);

  useEffect(() => {
    if (map.current === null && CurrentLng !== null && CurrentLat !== null) {
      mapboxgl.accessToken =
        "pk.eyJ1IjoiZGVubmlzY2hhbmc5MjgiLCJhIjoiY2tzanRjanRiMXpsbDJvbnVxdTgxNHh4byJ9.AcPK9uy9CBN1DUX8g6G-_Q";

      map.current = new mapboxgl.Map({
        container: mapContainer.current,
        style: "mapbox://styles/mapbox/streets-v11", // style URL
        center: [CurrentLng, CurrentLat], // starting position [lng, lat]
        zoom: 14 // starting zoom
      });
    }
  });

  if (map.current === null) {
    return <div ref={mapContainer} style={{ width: Width, height: Height }} />;
  } else {
    return (
      <div class="ui segment">
        <div class="ui active dimmer">
          <div class="ui indeterminate text loader"></div>
        </div>
        <p></p>
      </div>
    );
  }
};

export default Map;
