import React, { useEffect, useState } from "react";
import Images_preview from "./Image_preview";
import ImageEdit from "./ImageEdit.js";

const ImageSelector = ({ ImageSrc, onChange }) => {
  const [ImageSrcs, setImageSrcs] = useState(ImageSrc);
  const [ImageEditVisibility, setImageEditVisibility] = useState(false);

  useEffect(() => {
    setImageSrcs(ImageSrc);
  });

  const OnImageClicked = () => {
    setImageEditVisibility(true);
  };

  const OnImageDelete = index => {
    setImageEditVisibility(true);
    let _ImageSrcs = ImageSrcs;
    _ImageSrcs.splice(index, 1);
    setImageSrcs(_ImageSrcs);
    onChange(_ImageSrcs);
  };

  const OnImageEditClosed = () => {
    setImageEditVisibility(false);
  };

  console.log("typeof ImageSrcs");
  console.log(typeof ImageSrcs);
  return (
    <div className="two column row">
      <div className="eight wide column">
        {/* <Images_preview OnClick={OnImageClicked} Images={ImageSrcs} /> */}
      </div>
      <div className="eight wide column">
        <label for="fileSelector" className="fluid ui big button">
          <input
            type="file"
            id="fileSelector"
            className="fileSelector"
            data-target="file-uploader"
            accept="image/*"
            multiple
            onChange={onChange}
          />
          <i className="fluid images icon"></i>
          {"Add Images"}
        </label>
      </div>
      <ImageEdit
        OnClosed={OnImageEditClosed}
        OnDelete={OnImageDelete}
        Visible={ImageEditVisibility}
        Images={ImageSrcs}
      />
    </div>
  );
};
export default ImageSelector;
