import React, { useEffect, useState } from "react";
import Map from "./Map";
import geocode from "../Decode_Api/geocode";
import Spinner from "./Loading_Spinner";
import Pointer from "../Pointer/Pointer";
import AddButton from "../Pointer/AddBTN";
import Place_Definer from "../Place/Place_Definer";

const App = () => {
  const [CurrentLng, setCurrentLng] = useState(null);
  const [CurrentLat, setCurrentLat] = useState(null);
  const [Error, setError] = useState(null);
  const [State, setState] = useState(null);

  const setPosition = () => {
    window.navigator.geolocation.getCurrentPosition(
      position => {
        setCurrentLng(position.coords.longitude);
        setCurrentLat(position.coords.latitude);
      },
      err => {
        setError(err.message);
      }
    );
  };

  useEffect(() => {
    setPosition();
  }, []);

  useEffect(() => {
    console.log(`Lat:${CurrentLat}`);
    console.log(`Lng:${CurrentLng}`);
  });

  const OnMapCenterChange = (Lat, Lng) => {
    setCurrentLng(Lng);
    setCurrentLat(Lat);
  };

  const GetDetailAddress = async (Lat, Lng) => {
    const reponse = await geocode.get("", {
      params: {
        latlng: `${Lat},${Lng}`
      }
    });
    console.log(reponse.data.results);
  };

  const AddItem = () => {
    GetDetailAddress(CurrentLat, CurrentLng);
  };

  const MapInitialize = () => {
    if (CurrentLng !== null && CurrentLat !== null) {
      // initialized
      return (
        <div>
          {/* <Map
            Lat={CurrentLat}
            Lng={CurrentLng}
            OnCenterChange={OnMapCenterChange}
          />
          <Pointer />
          <AddButton OnClick={AddItem} /> */}
          if(null === null){<Place_Definer State="en" />}
        </div>
      );
    } else {
      return <Spinner message={Error} />;
    }
  };

  return MapInitialize();
};

export default App;
