import { file } from "@babel/types";
import React, { useEffect, useState } from "react";
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Select,
  TextArea,
  Transition
} from "semantic-ui-react";

const Place_Definer = ({ Card_Inform, visible, OnClosed, OnSumbit }) => {
  const options = [
    { key: "m", text: "Male", value: "male" },
    { key: "f", text: "Female", value: "female" },
    { key: "o", text: "Other", value: "other" }
  ];
  const [Images, setImages] = useState();
  useEffect(() => {
    const fileSelector = document.getElementById("fileSelector");
    if (fileSelector !== null)
      fileSelector.addEventListener("change", function() {
        getImgData();
      });
    function getImgData() {
      const files = fileSelector.files[0];
      if (files) {
        const fileReader = new FileReader();
        fileReader.readAsDataURL(files);
        fileReader.addEventListener("load", function() {
          imgPreview.style.display = "block";
          imgPreview.innerHTML = '<img src="' + this.result + '" />';
        });
      }
    }
  });
  return (
    <Transition visible={visible} animation="fade up" duration={1200}>
      <div
        style={{
          position: "absolute",
          bottom: "0%",
          width: "100%"
        }}
      >
        <div className="ui segment">
          <div className="ui form">
            <div className="ui grid">
              <div className="two column row">
                <div className="column">
                  <div className="field">
                    <label>Shop name</label>
                    <input
                      type="text"
                      name="SHOP-NAME"
                      placeholder="e.g., Ray's Bar"
                    />
                  </div>
                </div>
                <div className="column">
                  <div className="field">
                    <label>Shop Type</label>
                    <select className="ui search dropdown">
                      <option value="BAR">Bar</option>
                      <option value="RESTR">Restaurant</option>
                      <option value="SMKT">Supermarket</option>
                      <option value="BTQ">Boutique</option>
                    </select>
                  </div>
                </div>
              </div>
              <div className="two column row">
                <div className="eight wide column">
                  <h1>hello</h1>
                </div>

                <div className="eight wide column">
                  <label
                    for="fileSelector"
                    className="ui huge green right floated button"
                  >
                    <input
                      type="file"
                      id="fileSelector"
                      className="fileSelector"
                      data-target="file-uploader"
                      accept="image/*"
                    />
                    <i className="ui upload icon"></i>
                    Upload image
                  </label>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Place_Definer;
