import { file } from "@babel/types";
import React, { useEffect, useState } from "react";
import Image_preview from "./Image_preview";
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Select,
  TextArea,
  Transition
} from "semantic-ui-react";

const Place_Definer = ({ Card_Inform, visible, OnClosed, OnSumbit }) => {
  const [ImageSrc, setImageSrc] = useState([]);

  useEffect(() => {
    console.log(ImageSrc);
    console.log("Found Src Change");
  }, [ImageSrc]);

  const OnImageInputChange = () => {
    const fileSelector = document.getElementById("fileSelector");
    const Images = Array.from(fileSelector.files);
    if (Images) {
      let ImageBaseResult = [];
      for (let i = 0; i < Images.length; i++) {
        const reader = new FileReader();
        reader.readAsDataURL(Images[i]);
        reader.onload = function() {
          ImageBaseResult.push(reader.result);
        };
      }
      console.log("Set Src!!");
      setImageSrc(ImageBaseResult);
    }
  };

  return (
    // console.log("E");
    <Transition visible={visible} animation="fade up" duration={500}>
      <div
        style={{
          position: "absolute",
          bottom: "0%",
          width: "100%"
        }}
      >
        <div className="ui segment">
          <div className="ui form">
            <div className="ui grid">
              <div className="two column row">
                <div className="column">
                  <div className="field">
                    <label>Shop name</label>
                    <input
                      type="text"
                      name="SHOP-NAME"
                      placeholder="e.g., Ray's Bar"
                    />
                  </div>
                </div>
                <div className="column">
                  <div className="field">
                    <label>Shop Type</label>
                    <select className="ui search dropdown">
                      <option value="BAR">Bar</option>
                      <option value="RESTR">Restaurant</option>
                      <option value="SMKT">Supermarket</option>
                      <option value="BTQ">Boutique</option>
                    </select>
                  </div>
                </div>
              </div>
              <div className="two column row">
                <div className="eight wide column">
                  <Image_preview Images={ImageSrc} />
                </div>
                <div className="eight wide column">
                  <label
                    for="fileSelector"
                    className="ui tiny black right floated button"
                  >
                    <input
                      type="file"
                      id="fileSelector"
                      className="fileSelector"
                      data-target="file-uploader"
                      accept="image/*"
                      multiple
                      onChange={OnImageInputChange}
                    />
                    <i className="ui upload icon"></i>
                    Upload image
                  </label>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Place_Definer;
