import React, { useEffect, useState } from "react";
import Map from "./Map";
import Spinner from "./Loading_Spinner";
import Pointer from "../Pointer/Pointer";
import AddButton from "../Pointer/AddBTN";
import Place_Definer from "../Place_Define/Place_Definer";

const App = () => {
  const [CurrentLng, setCurrentLng] = useState(null);
  const [CurrentLat, setCurrentLat] = useState(null);
  const [Address, setAddress] = useState(null);
  const [DefinerVis, setDefinerVis] = useState(false);
  const [Error, setError] = useState(null);
  const [Card_State, setCard_State] = useState({
    id: "",
    Lat: "",
    Lng: "",
    Address: "",
    ShopName: "",
    ShopType: "",
    ImageSrc: []
  });

  console.log("App Rendering");

  const OnPlace_DefineChange = CardState => {
    setCard_State(Array.from(CardState));
    console.log("OnPlace_DefineChangeCalled");
    console.log(CardState.ImageSrc);
  };

  const setPosition = () => {
    window.navigator.geolocation.getCurrentPosition(
      position => {
        setCurrentLng(position.coords.longitude);
        setCurrentLat(position.coords.latitude);
      },
      err => {
        setError(err.message);
      }
    );
  };

  useEffect(() => {
    setPosition();
  }, []);

  const OnMapCenterChange = (Lat, Lng) => {
    setCurrentLng(Lng);
    setCurrentLat(Lat);
  };

  const GetDetailAddress = async (Lat, Lng) => {
    // const reponse = await geocode.get("", {
    //   params: {
    //     latlng: `${Lat},${Lng}`
    //   }
    // });
    // return reponse.data.results[0].formatted_address;
    return "";
  };

  const AddItem = () => {
    setAddress(GetDetailAddress(CurrentLat, CurrentLng));
    setDefinerVis(true);
  };

  const MapInitialize = () => {
    // if (CurrentLng !== null && CurrentLat !== null) {
    // initialized
    return (
      <div>
        {/* <Map
            Lat={CurrentLat}
            Lng={CurrentLng}
            OnCenterChange={OnMapCenterChange}
          /> */}
        {/* <Pointer /> */}
        {/* <AddButton OnClick={AddItem} /> */}
        <Place_Definer
          visible={true}
          Card_State={Card_State}
          onChange={OnPlace_DefineChange}
        />
      </div>
    );
    // } else {
    // return <Spinner message={Error} />;
    // }
  };

  return MapInitialize();
};

export default App;
