import { file } from "@babel/types";
import React, { useEffect, useState } from "react";
import ImageSelector from "./ImageSelector";
import {
  Button,
  Checkbox,
  Form,
  Input,
  Radio,
  Select,
  TextArea,
  Transition
} from "semantic-ui-react";

const Place_Definer = ({
  Card_State,
  visible,
  OnClosed,
  OnSumbit,
  onChange
}) => {
  const [id, setid] = useState(Card_State.id);
  const [ImageSrc, setImageSrc] = useState(Card_State.ImageSrc);
  const [ShopName, setShopName] = useState(Card_State.ShopName);
  const [ShopType, setShopType] = useState(Card_State.ShopType);
  const OnImageSrcChange = () => {
    const fileSelector = document.getElementById("fileSelector");
    const Images = Array.from(fileSelector.files);
    if (Images) {
      let ImageBaseResult = ImageSrc;
      for (let i = 0; i < Images.length; i++) {
        const reader = new FileReader();
        reader.readAsDataURL(Images[i]);
        reader.onload = function() {
          ImageBaseResult.push(reader.result);
        };
      }
      console.log("Set Src!!");
      setImageSrc(ImageBaseResult);
    }
  };
  console.log("rendering");

  const OnShopNameChange = E => {
    setShopName(E.target.value);
    onChange({
      id: { id },
      ShopName: { ShopName },
      ShopType: { ShopType },
      ImageSrc: { ImageSrc }
    });
  };
  const OnShopTypeChange = E => {
    console.log(E.target.value);
    setShopType(E.target.value);
    onChange({
      id: { id },
      ShopName: { ShopName },
      ShopType: { ShopType },
      ImageSrc: { ImageSrc }
    });
  };

  return (
    <Transition visible={visible} animation="fade up" duration={500}>
      <div
        style={{
          position: "absolute",
          bottom: "0%",
          width: "100%"
        }}
      >
        <div className="ui segment">
          <div className="ui form">
            <div className="ui grid">
              <div className="two column row">
                <div className="column">
                  <div className="field">
                    <label>Shop name</label>
                    <input
                      type="text"
                      name="SHOP-NAME"
                      placeholder="e.g., Ray's Bar"
                      value={ShopName}
                      onChange={OnShopNameChange}
                    />
                  </div>
                </div>
                <div className="column">
                  <div className="field">
                    <label>Shop Type</label>
                    <select
                      className="ui search dropdown"
                      value={ShopType}
                      onChange={OnShopTypeChange}
                    >
                      <option value="BAR">Bar</option>
                      <option value="RESTR">Restaurant</option>
                      <option value="SMKT">Supermarket</option>
                      <option value="BTQ">Boutique</option>
                    </select>
                  </div>
                </div>
              </div>
              <ImageSelector ImageSrc={ImageSrc} onChange={OnImageSrcChange} />
            </div>
          </div>
        </div>
      </div>
    </Transition>
  );
};

export default Place_Definer;
