import React, {useState } from "react";
import Map from "./Map";
import Spinner from "./Loading_Spinner";

const App = () => {
  const [CurrentLng, setLng] = useState(null);
  const [CurrentLat, setLat] = useState(null);
  const [Error, setError] = useState(null);

  // const setPosition = () => {
  //   window.navigator.geolocation.getCurrentPosition(
  //     position => {
  //       setCurrentLng(position.coords.longitude);
  //       setCurrentLat(position.coords.latitude);
  //     },
  //     err => {
  //       setError(err.message);
  //     }
  //   );
  // };

  // useEffect(() => {
  //   setPosition();
  // }, []);

  const OnMapCenterChange = (Lat, Lng) => {
    setLng("1");
    setLat("1");
    setError(Lat);
    // setCurrentLng(Lat);
    console.log(`Lat:${Lat}`);
    console.log(`CurrentLat:${Error}`);
    // console.log(`Lng:${CurrentLng}`);
  };

  if (CurrentLng !== null && CurrentLat !== null) {
    // console.log("hi");
    return (
      <div>
        <Map
          Lat={CurrentLat}
          Lng={CurrentLng}
          OnCenterChange={OnMapCenterChange}
        />
      </div>
    );
  } else {
    return <Spinner message={Error} />;
  }
};

export default App;
